<style lang="scss" scoped>
.ui-logo {
	position: relative;

	svg {
		width: auto;
		height: 20px;

		@include desktop {
			height: 32px;
		}

		g {
			transition: transform var(--duration-medium) var(--ease-in-out-circ);
			transition-delay: var(--duration-medium);

			&.dark {
				transform: translate(0, 100%);
			}

			&.light {
				transform: translate(0, 0);
			}
		}

		&:where(.theme-dark *) {
			g {
				&.dark {
					transform: translate(0, 0);
				}

				&.light {
					transform: translate(0, -100%);
				}
			}
		}
	}
}
</style>
